export type Locale = keyof typeof translations;

// Simplified TranslationKey to be a plain string
export type TranslationKey = string;

export function getTranslation(locale: Locale, key: TranslationKey, params?: Record<string, string | number>): string {
  const langTranslations = translations[locale] as any;
  const fallbackLangTranslations = translations.id as any;

  let translatedString: string;
  // This is a simplified lookup that doesn't handle nested keys.
  // It assumes keys are direct properties of the language object.
  if (langTranslations && typeof langTranslations === 'object' && key in langTranslations) {
    translatedString = langTranslations[key];
  } else if (fallbackLangTranslations && typeof fallbackLangTranslations === 'object' && key in fallbackLangTranslations) {
    translatedString = fallbackLangTranslations[key];
  } else {
    // Fallback to the key itself if not found
    translatedString = key;
  }

  if (params) {
    Object.entries(params).forEach(([paramKey, value]) => {
      translatedString = translatedString.replace(new RegExp(`{{${paramKey}}}`, 'g'), String(value));
    });
  }
  return translatedString;
}
